(928, 'John Henry', '', 'film', 2000, 'https://pictures.betaseries.com/films/affiches/original/39048.jpg', 'https://pictures.betaseries.com/films/backdrops/original/39048.jpg', 39048, 'tt0219105', 'null', 'A larger-than-life tale about the African-American hero born with a hammer in his hand. Join John Henry on a scenic journey from cotton country to the wilderness, where he finds men of all colors working together to build a great railroad.', 600, '2.00', 4, 'Animation,Drame,Familial'),
(929, 'John Mulaney ', 'John Mulaney et les kids', 'film', 2019, 'https://pictures.betaseries.com/films/affiches/original/74915.jpg', 'https://pictures.betaseries.com/films/backdrops/original/74915.jpg', 74915, 'tt11318624', 'omUnQ5ej6dY', 'John Mulaney et ses jeunes acolytes abordent en compagnie d\'invités spéciaux des sujets existentiels pour petits et grands au travers de chansons et de sketches.', 4200, '4.00', 8, 'Comédie,Familial'),
